// https://viem.sh/docs/abi/decodeAbiParameters
import { decodeAbiParameters, parseAbiParameters } from "npm:viem";

const values = decodeAbiParameters(
  [
    { name: "x", type: "string" },
    { name: "y", type: "uint" },
    { name: "z", type: "bool" },
  ],
  "",
);

const values2 = decodeAbiParameters(
  parseAbiParameters("string x, uint y, bool z"),
  "",
);

console.log(values);
console.log(values2);
